import React from 'react';

function App() {
  const [
    searchTerm,
    setSearchTerm,
  ] = React.useState('');

  // const inputRef = React.useRef();

  // "inputRef" možemo da koristimo tek nakon renderovanja komponente
  // zbog toga ga stavljamo u "useEffect"
  // React.useEffect(() => {
  //   // Uncomment me!
  //   inputRef.current.focus();
  // }, []);

  // 
  const inputRef = React.useRef();

  React.useEffect(() => {
      inputRef.current.focus();
  });


  return (
    <>
      <header>
        <img
          className="logo"
          alt="Foobar"
          src="https://sandpack-bundler.vercel.app/img/foogle.svg"
        />
      </header>
      <main>
        <form>
          <input
            ref={inputRef}
            value={searchTerm}
            onChange={(event) => {
              setSearchTerm(event.target.value);
            }}
          />
          <button>Search</button>
        </form>
      </main>
    </>
  );
}

export default App;
